import { useState } from 'react';

/**
 * A hook to force render a component.
 *
 * @returns {Function} — set value
 */

var useForceUpdate = () => {
  var [value, setValue] = useState(0);
  return () => setValue(value + 1);
};

export { useForceUpdate };
